mapper.select:
SELECT p.`id`, p.`name`, p.`content`, p.`user_id` AS userId, p.`start_dt` AS startDate, p.`end_dt` AS endDate, p.`updated`, p.`created`, u.name AS writer FROM `tb_project` p, tb_user u WHERE p.user_id = u.id
AND p.`name` LIKE CONCAT('%', #{name}, '%')
ORDER BY p.`id` DESC LIMIT #{size} OFFSET #{offset}
