mapper.select:
SELECT p.id AS projectId, p.name AS projectName, u.id AS userId, u.name AS userName, u.email FROM tb_project p LEFT JOIN tb_project_member pm ON pm.project_id = p.id LEFT JOIN tb_user u ON u.id = pm.user_id
WHERE p.id IN ( SELECT pm2.project_id FROM tb_project_member pm2 WHERE pm2.user_id = #{userId} )
ORDER BY p.id;
